import React, {useState, useMemo} from 'react';
import data from './data';

const pageSize = 10;
const sortOptions = [
  {value: 'time', label: 'Время'},
  {value: 'calories', label: 'Калории'},
  {value: 'pulse', label: 'Пульс'}
];

function App(){
  const [filters, setFilters] = useState({
    workout: '',
    timeFrom: '', timeTo: '',
    caloriesFrom: '', caloriesTo: '',
    pulseFrom: '', pulseTo: ''
  });

  const [sortLevels, setSortLevels] = useState([
    {field: '', desc: false},
    {field: '', desc: false},
    {field: '', desc: false},
  ]);

  const [page, setPage] = useState(1);

  const handleFilterChange = e=>{
    setFilters({...filters, [e.target.name]: e.target.value});
  };

  const handleClearFilters = ()=>{
    setFilters({
      workout: '',
      timeFrom:'', timeTo:'',
      caloriesFrom:'', caloriesTo:'',
      pulseFrom:'', pulseTo:''
    });
  };

  const filteredData = useMemo(()=>{
    return data.filter(item=>{
      if(filters.workout && !item.workout.toLowerCase().includes(filters.workout.toLowerCase())) return false;
      const numCheck = (val, from, to)=>{
        if(from && val < parseFloat(from)) return false;
        if(to && val > parseFloat(to)) return false;
        return true;
      };
      if(!numCheck(item.time, filters.timeFrom, filters.timeTo)) return false;
      if(!numCheck(item.calories, filters.caloriesFrom, filters.caloriesTo)) return false;
      if(!numCheck(item.pulse, filters.pulseFrom, filters.pulseTo)) return false;
      return true;
    });
  },[filters]);

  const sortedData = useMemo(()=>{
    let arr = [...filteredData];
    const compare = (a,b,field,desc)=>{
      if(a[field] < b[field]) return desc?1:-1;
      if(a[field] > b[field]) return desc?-1:1;
      return 0;
    };
    // apply in reverse order (third -> first)
    for(let i=sortLevels.length-1;i>=0;i--){
      const {field,desc} = sortLevels[i];
      if(field){
        arr.sort((a,b)=>compare(a,b,field,desc));
      }
    }
    return arr;
  },[filteredData, sortLevels]);

  const totalPages = Math.max(1, Math.ceil(sortedData.length / pageSize));
  const pageData = sortedData.slice((page-1)*pageSize, page*pageSize);

  const handleSortField = (idx, field)=>{
    const newLevels = [...sortLevels];
    newLevels[idx].field = field;
    if(!field){ // clear subsequent levels
      for(let j=idx+1;j<3;j++){
        newLevels[j]={field:'',desc:false};
      }
    }
    setSortLevels(newLevels);
  };

  const handleSortDesc = (idx, desc)=>{
    const newLevels=[...sortLevels];
    newLevels[idx].desc = desc;
    setSortLevels(newLevels);
  };

  const resetSort = ()=>{
    setSortLevels([
      {field:'',desc:false},
      {field:'',desc:false},
      {field:'',desc:false}
    ]);
  };

  const prevPage = ()=> setPage(p=>Math.max(1,p-1));
  const nextPage = ()=> setPage(p=>Math.min(totalPages,p+1));

  return (
    <div>
      {/* FILTER PANEL */}
      <div className="panel">
        <h3>Фильтр</h3>
        <div>
          <label>Тренировка: <input name="workout" value={filters.workout} onChange={handleFilterChange}/></label>
        </div><br/>
        <div>
          <label>Время (от): <input name="timeFrom" value={filters.timeFrom} onChange={handleFilterChange}/></label>
          <label> до: <input name="timeTo" value={filters.timeTo} onChange={handleFilterChange}/></label>
        </div><br/>
        <div>
          <label>Калории (от): <input name="caloriesFrom" value={filters.caloriesFrom} onChange={handleFilterChange}/></label>
          <label> до: <input name="caloriesTo" value={filters.caloriesTo} onChange={handleFilterChange}/></label>
        </div><br/>
        <div>
          <label>Пульс (от): <input name="pulseFrom" value={filters.pulseFrom} onChange={handleFilterChange}/></label>
          <label> до: <input name="pulseTo" value={filters.pulseTo} onChange={handleFilterChange}/></label>
        </div><br/>
        <button onClick={()=>setPage(1)}>Найти</button>&nbsp;
        <button onClick={handleClearFilters}>Сброс</button>
      </div>
      {/* SORT PANEL */}
      <div className="panel">
        <h3>Сортировка</h3>
        { [0,1,2].map(idx=>(
          <div key={idx} style={{marginBottom:'8px'}}>
            <label>{['Первый','Второй','Третий'][idx]} уровень:&nbsp;
              <select
                value={sortLevels[idx].field}
                onChange={(e)=>handleSortField(idx,e.target.value)}>
                <option value="">Нет</option>
                {sortOptions.map(o=><option key={o.value} value={o.value}>{o.label}</option>)}
              </select>
            </label>
            &nbsp;
            <label><input type="checkbox" checked={sortLevels[idx].desc}
              onChange={(e)=>handleSortDesc(idx,e.target.checked)}
              disabled={!sortLevels[idx].field}
            /> по убыванию?</label>
          </div>
        )) }
        <button onClick={()=>{setPage(1);}}>Сортировать</button>&nbsp;
        <button onClick={resetSort}>Сбросить</button>
      </div>

      {/* TABLE */}
      <table>
        <thead>
          <tr>
            <th>№</th>
            <th>Дата</th>
            <th>Тренировка</th>
            <th>Время (мин)</th>
            <th>Калории</th>
            <th>Пульс (ср.)</th>
          </tr>
        </thead>
        <tbody>
          {pageData.map(row=>(
            <tr key={row.id}>
              <td>{row.id}</td>
              <td>{row.date}</td>
              <td>{row.workout}</td>
              <td>{row.time}</td>
              <td>{row.calories}</td>
              <td>{row.pulse}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* PAGINATION */}
      <div className="controls">
        <button onClick={prevPage} disabled={page===1}>Prev</button>
        &nbsp; Стр. {page} / {totalPages} &nbsp;
        <button onClick={nextPage} disabled={page===totalPages}>Next</button>
      </div>
    </div>
  );
}

export default App;
